function Shop() {
   const now = new Date();
    const actualHour = now.getHours();
    const actualMinute = now.getMinutes();
    const displayMinute = actualMinute.toString().padStart(2, '0');
    const isOpen = actualHour >= 9 && actualHour < 18;
    return (
        <div>
            <h1>Welcome to our Shop!</h1>

            <p>Current hour: {actualHour}h{displayMinute}</p>
            
            <p>{isOpen ? "We are open!" : "Sorry, we are closed."}</p>
        </div>
    );
}

export default Shop;
