import React, { Component } from 'react';

class ProductPreview extends Component {

    constructor(props) {
        super(props);
        // J'ajoute un petit state pour que le bouton fonctionne et déclenche l'update
        this.state = { compteur: 0 };
    }

    // 1. Se déclenche UNE SEULE FOIS, juste après que le composant s'affiche
    componentDidMount() {
        // Attention : Tu vas avoir cette alerte pour CHAQUE produit de ta liste !
        alert("the component was just displayed (Monté)");
    }

    // 2. Se déclenche à chaque fois que le composant change (props ou state)
    componentDidUpdate() {
        alert("component was updated (Mis à jour)");
    }

    // 3. Se déclenche juste avant que le composant disparaisse de l'écran
    componentWillUnmount() {
        alert("le composant va être détruit");
    }

    // Fonction pour changer le state quand on clique (provoque un Update)
    handleClick = () => {
        this.setState({ compteur: this.state.compteur + 1 });
    }

    render() {
        return (
            <div className="ProductPreview" style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
                <img 
                    src={this.props.image} 
                    alt={this.props.name} 
                    width="150" 
                    style={{display: "block", margin: "0 auto"}} 
                />
                <h3>{this.props.name}</h3>
                <p><strong>{this.props.price} €</strong></p>
                <p>{this.props.description}</p>
                
                {/* Le bouton pour tester l'étape 2 et 3 */}
                <button onClick={this.handleClick}>
                    Clique-moi (Test Update) : {this.state.compteur}
                </button>
            </div> 
        );
    }
}

export default ProductPreview;
