import React, { Component } from 'react';

class GuessNumber extends Component {
  constructor(props) {
    super(props);
    this.state = {
      targetNumber: null, 
      message: ''         
    };
  }

  // Lifecycle method: Runs when the component mounts (launches)
  componentDidMount() {
    this.generateRandomNumber();
  }

  // Helper to generate a random number (e.g., between 1 and 100)
  generateRandomNumber = () => {
    // Math.random() generates 0-1. We scale it to 1-100.
    const randomNum = Math.floor(Math.random() * 100) + 1;
    this.setState({ targetNumber: randomNum });
    console.log("Secret number:", randomNum); // For debugging purposes
  };

  // Logic to handle user input
  handleInput = (e) => {
    const userGuess = parseInt(e.target.value, 10);
    const { targetNumber } = this.state;

    // Check if input is empty or not a number
    if (isNaN(userGuess)) {
      this.setState({ message: '' });
      return;
    }

    // Comparison Logic requested in the prompt
    if (userGuess > targetNumber) {
      this.setState({ message: "le nombre est plus petit" });
    } else if (userGuess < targetNumber) {
      this.setState({ message: "le nombre est plus grand" });
    } else {
      this.setState({ message: "c'est juste" });
    }
  };

  render() {
    return (
      <div style={{ padding: '20px', border: '1px solid #ccc' }}>
        <h2>Jeu du Nombre Myst√®re</h2>
        
        <label htmlFor="guessInput">Devinez le nombre (1-100) : </label>
        <br />
        
        {/* Using onInput as requested */}
        <input 
          type="number" 
          id="guessInput" 
          onInput={this.handleInput} 
          placeholder="Entrez un nombre..."
        />

        {/* Display the message from state */}
        <p style={{ fontWeight: 'bold', marginTop: '10px' }}>
          {this.state.message}
        </p>
      </div>
    );
  }
}

export default GuessNumber;
