import React, { Component } from 'react'; // Essential for Class Components
import './App.css';
import Title from './Title';
import ProductList from './ProductList';
import Magasin from './Magasin';
import CounterPerson from './CounterPerson';
import GuessNumber from './GuessNumber';

// 3. Création des composants simples
const Accueil = () => <h3>Bienvenue sur la page d'Accueil</h3>;
const Contact = () => <h3>Page de Contact</h3>;

// 1. Transformer le composant App en Class Component
class App extends Component {
  constructor(props) {
    super(props);
    // 2. Créer un state avec clé "currentPage"
    this.state = {
      currentPage: 'accueil'
    };
  }

  // Fonction pour gérer le clic (Step 5)
  handleNavigate = (e, pageName) => {
    e.preventDefault(); // Penser au preventDefault
    this.setState({ currentPage: pageName });
  }

  render() {
    return (
      <div className="App">
        {/* Menu de navigation pour tester */}
        <nav style={{ padding: '10px', borderBottom: '1px solid #ccc', marginBottom: '20px' }}>
          {/* 5. Ajouter un lien vers contact */}
          <a 
            href="/contact" 
            onClick={(e) => this.handleNavigate(e, 'contact')}
            style={{ marginRight: '10px' }}
          >
            Aller vers Contact
          </a>

          {/* Lien pour revenir à l'accueil (utile pour tester) */}
          <a 
            href="/" 
            onClick={(e) => this.handleNavigate(e, 'accueil')}
          >
            Retour Accueil
          </a>
        </nav>

        {/* 4. Rendu Conditionnel */}

        {/* Affichage si currentPage est "accueil" */}
        {this.state.currentPage === 'accueil' && (
          <div>
            <Accueil />
            {/* J'ai gardé vos anciens composants ici pour qu'ils s'affichent sur l'accueil */}
            <Magasin />
            <CounterPerson />
            <GuessNumber />
            <Title />
            <h1>Shop section</h1>
            <h2>Product List</h2>
            <ProductList />
          </div>
        )}

        {/* Affichage si currentPage est "contact" */}
        {this.state.currentPage === 'contact' && (
          <div>
            <Contact />
            <p>Ceci est le contenu de la page contact.</p>
          </div>
        )}

      </div>
    );
  }
}

export default App;
