import React, { Component } from 'react';

// 1. On transforme la fonction en Classe qui étend Component
class ProductPreview extends Component {
  
  // 2. On ajoute le constructeur comme demandé dans l'énoncé
  constructor(props) {
    super(props); // On appelle super(props) pour initialiser "this.props"
  }

  render() {
    return (
      <div className="product-preview" style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
        {/* On accède maintenant aux props via "this.props" */}
        <img 
          src={this.props.image} 
          alt={this.props.name} 
          style={{ width: '100px', height: '100px', objectFit: 'cover' }} 
        />
        
        <h3>{this.props.name}</h3>
        <p>Prix : {this.props.price} €</p>
        
        {/* 3. L'énoncé demande de vérifier l'affichage de la description ici */}
        <p>{this.props.description}</p>
      </div>
    );
  }
}

export default ProductPreview;
