import React, { Component } from 'react';
import './App.css';
import Title from './Title';
import ProductList from './ProductList';
import Magasin from './Magasin';
import CounterPerson from './CounterPerson';
import GuessNumber from './GuessNumber';
import Contact from './Contact';

const Accueil = () => <h3>Bienvenue sur la page d'Accueil</h3>;
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 'accueil'
    };
  }

  handleNavigate = (e, pageName) => {
    e.preventDefault();
    this.setState({ currentPage: pageName });
  }

  render() {
    return (
      <div className="App">
        <nav style={{ padding: '10px', borderBottom: '1px solid #ccc', marginBottom: '20px' }}>
          <a 
            href="/contact" 
            onClick={(e) => this.handleNavigate(e, 'contact')}
            style={{ marginRight: '10px' }}
          >
            Contact
          </a>

          <a 
            href="/" 
            onClick={(e) => this.handleNavigate(e, 'accueil')}
          >
            Accueil
          </a>
        </nav>

        {this.state.currentPage === 'accueil' && (
          <div>
            <Accueil />

            <Magasin />
            <CounterPerson />
            <GuessNumber />
            <Title />
            <h1>Shop section</h1>
            <h2>Product List</h2>
            <ProductList />
          </div>
        )}

        {/* Affichage si currentPage est "contact" */}
        {this.state.currentPage === 'contact' && (
          <div>
            <Contact />
          </div>
        )}

      </div>
    );
  }
}

export default App;
