import React, { Component } from 'react';

class CounterPerson extends React.Component {   
    constructor(props) {
        super(props);
        this.state = { compteur: 0 };
    }


    handleClick = () => {
            this.setState({ compteur: this.state.compteur});
            this.setState({ add_person: this.state.add_person + 1 });
            this.setState({ rm_person: this.state.rm_person - 1 })
    }

    render() {
        return (
            <div className="CounterPerson">
                <p>Current Count: {this.state.compteur}</p>
                <button onClick={this.handleClick}>
                    Add Person : {this.state.add_person}
                </button>
                <button onClick={this.handleClick}>
                    Remove Person : {this.state.rm_person}
                </button>

            </div>
        )
    }
}
export default CounterPerson;
