import React from 'react';

function Shop() {
    // 1. On calcule l'heure À L'INTÉRIEUR du composant
    const now = new Date();
    const actualHour = now.getHours();

    // 2. On détermine si c'est ouvert
    // Note: j'ai mis < 18 car souvent on ferme à 18h00 pile (donc ouvert tant qu'il est 17h59)
    const isOpen = actualHour >= 9 && actualHour < 18;

    return (
        <div>
            <h1>Welcome to our Shop!</h1>
            
            {/* 3. On peut maintenant utiliser actualHour car il est défini au-dessus */}
            <p>Current hour: {actualHour}h</p>
            
            <p>{isOpen ? "We are open!" : "Sorry, we are closed."}</p>
        </div>
    );
}

export default Shop;
